#!/bin/bash
export CUDA_VISIBLE_DEVICES=0
echo "Training Global Koopman..."
python train.py --config-file configs/prompt/cub.yaml MODEL.TYPE "vit" DATA.BATCH_SIZE "96" MODEL.PROMPT.KOOPMAN_DIM "256" MODEL.PROMPT.KOOPMAN_WEIGHT "0.6" MODEL.PROMPT.LYAPUNOV_WEIGHT "0.05" MODEL.PROMPT.DEEP "True" MODEL.PROMPT.DROPOUT "0.1" DATA.FEATURE "sup_vitb16_imagenet21k" DATA.NAME "CUB" DATA.NUMBER_CLASSES "200" SOLVER.BASE_LR "0.25" SOLVER.WEIGHT_DECAY "0.001" SEED 42 MODEL.MODEL_ROOT "/home/wjz/data" DATA.DATAPATH "/home/wjz/data/CUB_200_2011" OUTPUT_DIR "output_global" MODEL.PROMPT.KOOPMAN_MOD "global" ANALYSIS.ENABLE_AUTO_ANALYSIS True ANALYSIS.MODEL_NAME "global_koopman" ANALYSIS.NUM_TRAJECTORY_BATCHES 20
echo "Training Layer-wise Koopman..."
python train.py --config-file configs/prompt/cub.yaml MODEL.TYPE "vit" DATA.BATCH_SIZE "96" MODEL.PROMPT.KOOPMAN_DIM "256" MODEL.PROMPT.KOOPMAN_WEIGHT "0.6" MODEL.PROMPT.LYAPUNOV_WEIGHT "0.05" MODEL.PROMPT.DEEP "True" MODEL.PROMPT.DROPOUT "0.1" DATA.FEATURE "sup_vitb16_imagenet21k" DATA.NAME "CUB" DATA.NUMBER_CLASSES "200" SOLVER.BASE_LR "0.25" SOLVER.WEIGHT_DECAY "0.001" SEED 42 MODEL.MODEL_ROOT "/home/wjz/data" DATA.DATAPATH "/home/wjz/data/CUB_200_2011" OUTPUT_DIR "output_layerwise" MODEL.PROMPT.KOOPMAN_MOD "layerwise" ANALYSIS.ENABLE_AUTO_ANALYSIS True ANALYSIS.MODEL_NAME "layerwise_koopman" ANALYSIS.NUM_TRAJECTORY_BATCHES 20